            <div class="box3content3">
              <h1>are you Tutor?</h1>
            </div>
          </div>
        </div> */}
        <div calss="container">
        <img src={img2} alt="Avatar" class="image" />
              <div></div>
        </div>
              <div class="box12">
           
            <div class="box1content11">
              <h1>are you student?</h1>
            </div>
          </div>
        <div >
    
          <div class="box22">
            <img src={img2} alt="Avatar" class="imagelaptop2" />
            <div class="box2content22">
              <h1>are you Parents?</h1>
            </div>
          </div>
          <div class="box33">
            <img src={img2} alt="Avatar" class="imagelaptop2" />
            <div class="box3content33">
              <h1>are you Tutor?</h1>
            </div>
          </div>
        </div>

      </div>
    </div>
  );
}

export default Home;
